<script>
import Vue from 'vue';
import axios from 'axios'
import VueAxios from 'vue-axios'
Vue.use(VueAxios,axios)
export default {
    name:'Options',
    data() {
      return {
        list:[],
      }
    },
    mounted(){
      Vue.axios.get('https://fakestoreapi.com/products/category/jewelery')
      .then((res)=>{
        this.list=res.data
        console.warn(res.data)
      })
    },methods: {
      sortby(){
      this.list.sort((a,b)=>a.price<b.price?-1:-1)
      console.log("sorted")
      },sortbyl(){
      this.list.sort((a,b)=>a.price<b.price?-1:1)
      console.log("sorted")
      },sortbyr(){
      this.list.sort((a,b)=>a.rating.rate<b.rating.rate?-1:1)
      console.log("sorted")
      }
    },
}
</script>
